(()=>{async function l(e="",r={}){try{let t=await fetch(e,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(r)});if(!t.ok)throw new Error("Network response was not OK");return t.json()}catch(t){return console.error("There has been a problem with your fetch operation:",t),!1}}function m(e,r){let t=e.getAttribute("action"),n=new FormData(r),a={};if(n.forEach((o,u)=>{a[u]=o}),!n.get("email"))return;let c=document.querySelector("#form-success"),i=document.querySelector("#form-error");l(t,a).then(o=>{o?c?(c.classList.remove("hidden"),e.reset()):console.log("Signup successful!",o):i&&i.classList.remove("hidden")})}var s=document.querySelector("#signUpForm");s&&s.addEventListener("submit",e=>{e.preventDefault(),m(s,e.target)});})();
